class SantaElevator
  def initialize(instructions)
    @instructions = instructions
    @floor = 0
    @position_enters_basement_at = nil

    operate_elevator
    stats_presenter
  end

  def increment_floor(char)
    if char == "("
      return 1
    elsif char == ")"
      return -1
    end
  end

  def check_if_in_basement(index)
    if @position_enters_basement_at == nil && @floor < 0
      @position_enters_basement_at = index + 1
    end
  end

  def operate_elevator
    @instructions.split("").each_with_index do |char, index|
      @floor += increment_floor(char)
      check_if_in_basement(index)
    end
  end

  def stats_presenter
    puts "Santa ends up on floor: #{@floor}"
    puts "Position that first puts Santa in the basement: #{@position_enters_basement_at}"
  end

end

SantaElevator.new(instructions)
